functions.likeAndUnlikeCard:
function (card) {  let action =  {};
  if(card.cardType === 'news') {
    if (card.canLike) {
      action.query = "mutation { post { like(id: $templateContext->item.id) }}"
    } 
    else {
      action.query = "mutation { post { unlike(id: $templateContext->item.id) }}" 
    }
  } else {
    if (card.canLike) {
      action.query = "mutation { event { like(id: $templateContext->item.id) }}"
    } 
    else {
      action.query = "mutation { event { unlike(id: $templateContext->item.id) }}"  
    }
  }

  if (card.canLike) {
    action.postAction = "templateContext.item.canLike = false; templateContext.item.likersCount++; $actionHelper.showToast('You Liked this post');"
  } else {
    action.postAction = "templateContext.item.canLike = true; if(templateContext.item.likersCount > 0) { templateContext.item.likersCount--; }; $actionHelper.showToast('Post Like Removed');"
  }
  action.item = card;
  this.$actionHelper.callApi(action);
}

functions.saveAndUnsaveCard:
function (card) {  let action =  {};
  if(card.cardType === 'news') {
    if (card.canSave) {
      action.query = "mutation { post { save(id: $templateContext->item.id) }}" 
    } 
    else {
      action.query = "mutation { post { unsave(id: $templateContext->item.id) }}"
    }
  } else {
    if (card.canSave) {
      action.query = "mutation { event { save(id: $templateContext->item.id) }}"  
    } 
    else {
      action.query = "mutation { event { unsave(id: $templateContext->item.id) }}"
    }
  }

  if (card.canSave) {
    action.postAction = "templateContext.item.canSave = false; $actionHelper.notifiySubscribers(['saved-post']); $actionHelper.showToast('Post saved successfully');"
  } else {
    action.postAction = "templateContext.item.canSave = true; $actionHelper.notifiySubscribers(['saved-post']); $actionHelper.showToast('Removed from saved posts successfully');"
  }
  action.item = card;
  this.$actionHelper.callApi(action);          
}

