functions.likeComment:
function(comment) {  let action =  {};
  action.query = "mutation { comment { like(id: $templateContext->additionalParameters.entityId) }}"
  action.postAction = "templateContext.additionalParameters.comment.checkIfCanLike = false; templateContext.additionalParameters.comment.numberOfLikes++;  templateContext.additionalParameters.comment.checkIfCanUnlike = true; $actionHelper.showToast('Comment Liked');"
  action.item = comment;
  action.additionalParameters = {'entityId': comment.id, 'comment': comment};
  this.$actionHelper.callApi(action);
}

functions.unlikeComment:
function(comment) {  let action =  {};
  action.query = "mutation { comment { unlike(id: $templateContext->additionalParameters.entityId) }}"
  action.postAction = "templateContext.additionalParameters.comment.checkIfCanLike = true; if(templateContext.additionalParameters.comment.numberOfLikes > 0) { templateContext.additionalParameters.comment.numberOfLikes--; }; templateContext.additionalParameters.comment.checkIfCanUnlike = false; $actionHelper.showToast('Like removed');"
  action.item = comment;
  action.additionalParameters = {'entityId': comment.id, 'comment': comment };
  this.$actionHelper.callApi(action);
}

functions.afterInit:
function () {  if($actionHelper.followSubject) {
    this.followSubscription = $actionHelper.followSubject.subscribe((followInfo) => {
      if(followInfo) {
        $actionHelper.checkMainEntity(this.dataSource, followInfo);                
        $actionHelper.checkFollowList(this.dataSource.teamMembers, followInfo);
        $actionHelper.checkFollowList(this.dataSource.admins, followInfo);
        $actionHelper.checkFollowList(this.dataSource.currentAffiliations, followInfo);
      }
    });
  }
}

functions.uploadPhoto:
function ($id, $event) {  
  this.$actionHelper.$loaderService.present().then(() => 
    this.getGraphQL(`
      mutation($id: String!, $photo: Upload!) {
        company {
          uploadLogo(id: $id, photo: $photo)
        }
      }
    `, {id: $id, photo: $event}).then(uploadResponse => {              
        $actionHelper.showToast('Image updated successfully');
        $actionHelper.notifiySubscribers(['edit-company'], false);              
    }).finally(() => {
      this.$actionHelper.$loaderService.dismiss();
    })   
  )
}

