functions.afterInit:
function () {  const initialDataSource = this.$actionHelper.uiDefinition.initialDataSource 
  if(initialDataSource && initialDataSource.keywords && initialDataSource.keywords.length > 0) {
    initialDataSource.keywords.forEach(x => {x.selected = false; x.isSelected = false});  
  }
  if(initialDataSource && initialDataSource.brands && initialDataSource.brands.length > 0) {
    initialDataSource.brands.forEach(x => {x.selected = false; x.isSelected = false});  
  }
  if(initialDataSource && initialDataSource.countries && initialDataSource.countries.length > 0) {
    initialDataSource.countries.forEach(x => {x.selected = false; x.isSelected = false});        
  }
}

functions.addNewKeyword:
function ($keyword, parentComponent) {  this.$actionHelper.$loaderService.present().then(() => 
    this.getGraphQL(`
      mutation($keyword: String!) {
        company {
          addKeyword(title: $keyword) {
            lookupId
            name
          }
        }
      }
    `, {keyword: $keyword}).then(data => {    
        this.$actionHelper.getKeywordsLookups();
        const newOption = _.get(data, 'company.addKeyword', null);
        if(newOption) {
          newOption.isSelected = true;
          parentComponent.options.unshift(newOption);
          parentComponent.initialOptions.unshift(newOption);
          parentComponent.getInitialList();  
        }
    }).finally(() => {
      this.$actionHelper.$loaderService.dismiss();
    })   
  )
}

functions.openKeywords:
function($event,form, formGroup, initialDataSource, formsData) {  this.$actionHelper.openMultiSelectModal('Keywords', 
    'Tap to add a new keyword', 'Keyword already exists', $event.element, formGroup, initialDataSource.keywords, $event.element.value, false, formsData, true, true, initialDataSource, 
    (newKeyword, parentComponent) => { this.addNewKeyword(newKeyword, parentComponent) })    
}

