functions.getFullTree:
async function (dataSource) {  const parentBranchesResponse = await $dynamicUiExtraService.getCompanyParentBranches($activatedRoute.snapshot.params.id, $actionHelper);
  const rootBranchId = parentBranchesResponse.rootBranchId;
  const hasParentBranch = parentBranchesResponse.hasParentBranch;
  const parentBranchId = parentBranchesResponse.parentBranchId;

  let branchesTree = [];
  if(hasParentBranch) {
    branchesTree = await $dynamicUiExtraService.prepareCompanyChildBranches(rootBranchId, $actionHelper, $activatedRoute.snapshot.params.id);
  }

  dataSource.branchesTree = branchesTree;
  dataSource.parentBranchId = parentBranchId;  
}

functions.removeBranch:
function ($id, $branchId, $dynamicUiExtraService, dataSource) {  this.$actionHelper.$loaderService.present().then(() => 
    this.getGraphQL(`
      mutation($id: String!, $branchId: String!) {
        company {
          removeParentEntity(id: $id, parentId: $branchId)
        }
      }
    `, {id: $id, branchId: $branchId}).then((data) => {
        $actionHelper.showToast('Company link removed successfully');
        this.getFullTree(dataSource);


        this.getOutgoingRequest(dataSource);

        $actionHelper.notifiySubscribers(['manage-company-branches']);
      }).finally(() => {
      this.$actionHelper.$loaderService.dismiss();
    })   
  )
}

functions.getOutgoingRequest:
function (dataSource) {    this.getGraphQL(`
      query 
        { 
          company(id: $dynamicUiVariables->$activatedRoute.snapshot.params.id) {
            outgoingBranchesRequests(after: null, first: $dynamicUiVariables->initialDataSource.generalInfo.listPageSize) {
              items {
                company {
                  ...companyCommonProperties
                }
              }
              cursor
            }  
          }
        }              
    `).then((data) => {
        dataSource.outgoingBranchesRequests = _.get(data, 'company.outgoingBranchesRequests.items', []);
        dataSource.outgoingBranchesRequestsCursor = _.get(data, "company.outgoingBranchesRequests.cursor");
    })
  }

