functions.markNotificationAsRead:
function(item) {  if(item.readed) {
    if(item.objectType === 'Conversation') {
      $actionHelper.navigateTo('conversationMessages/' + item.conversationTargetType +  '/'+ item.conversationTargetId + '/' + item.conversationSenderMailboxId + '/' + item.conversationReceiverMailboxId);
    } else if(item.objectType === 'Comment') {
      $actionHelper.navigateToDomain(item.commentActivityType, item.commentActivityId);
    } else {
      $actionHelper.navigateToDomain(item.objectType, item.parentDomainId);
    }
  } else {
    let action =  {};
    action.query = "mutation { notification { markNotificationAsRead(notificationId: $templateContext->item.id) }}"
    if(item.objectType === 'Conversation') {
      action.postAction = "$actionHelper.$notificationService.getUnreadNotification();$actionHelper.notifiySubscribers(['manage-company', 'manage-organization'], true); templateContext.item.readed = true; $actionHelper.navigateTo('conversationMessages/' + templateContext.item.conversationTargetType +  '/'+ templateContext.item.conversationTargetId + '/' + templateContext.item.conversationSenderMailboxId + '/' + templateContext.item.conversationReceiverMailboxId)"
    } else if(item.objectType === 'Comment') {
      action.postAction = "$actionHelper.$notificationService.getUnreadNotification();$actionHelper.notifiySubscribers(['manage-company', 'manage-organization'], true); templateContext.item.readed = true; $actionHelper.navigateToDomain(templateContext.item.commentActivityType, templateContext.item.commentActivityId)"
    } else {
      action.postAction = "$actionHelper.$notificationService.getUnreadNotification();$actionHelper.notifiySubscribers(['manage-company', 'manage-organization'], true); templateContext.item.readed = true; $actionHelper.navigateToDomain(templateContext.item.objectType, templateContext.item.parentDomainId)"
    }
    action.item = item;
    this.$actionHelper.callApi(action);
  }
}

functions.saveNotificationSettings:
function(initiallNotifications, notificationTypeSettings) {  for (const notificationType of initiallNotifications) {
    for (const notificationSection of notificationTypeSettings) {
      if(notificationType.group && notificationType.group.toLowerCase() === 'general') {
        if (notificationSection.key === notificationType.description) {
          notificationType.pushEnabled = notificationSection.pushEnabled;
          notificationType.emailEnabled = notificationSection.emailEnabled;            
        }
      } else {
        if (notificationSection.key === notificationType.group) {
          notificationType.pushEnabled = notificationSection.pushEnabled;
          notificationType.emailEnabled = notificationSection.emailEnabled;
        }
      }
    }
  }

  const updatedNotifications = [];
  for(let notification of initiallNotifications) {
    updatedNotifications.push({'NotificationTypeId': notification.id, 'PushEnabled': notification.pushEnabled , 'EmailEnabled': notification.emailEnabled});
  }

  let action =  {};
  action.query = "mutation { notification { updateNotificationSettings(settings: $dynamicUiExpression((|prepareNotificationSettings(templateContext.additionalParameters, 'notificationSettings')|)))}}"
  action.postAction = "$actionHelper.goBackPage();  $actionHelper.showToast('Notification Settings have been updated successfully')"        
  action.additionalParameters = {'notificationSettings': updatedNotifications};
  this.$actionHelper.callApi(action);
}

functions.sendMessage:
function(dataSource, message, targetId, targetType) {  let action =  {};
  const newMessage = {'myMessage': true, 'conversationOwnerType': 'Profile', 'conversationOwnerId': dataSource.senderId, 'conversationOwnerImage': dataSource.senderImage, 'conversationOwnerName': dataSource.senderName, 'messageText': message, 'messageDate': new Date() };
  action.query = "mutation { $dynamicUiExpression((|$templateContext->additionalParameters.targetType|)) { sendMessage(id: $templateContext->additionalParameters.targetId, text: $templateContext->additionalParameters.text, conversationId: $templateContext->additionalParameters.conversationId, fromMailBoxId: $templateContext->additionalParameters.fromMailBoxId, toMailBoxId: $templateContext->additionalParameters.toMailBoxId ) { id } }}"
  action.postAction = "templateContext.additionalParameters.dataSource.messages.push(templateContext.additionalParameters.newMessage); $actionHelper.notifiySubscribers(['add-message']); $actionHelper.scrollToBotton('conversationMailboxDiv')"
  action.additionalParameters = {dataSource: dataSource, newMessage: newMessage,'targetType': _.camelCase(targetType), 'targetId':targetId, 'text': message, 'conversationId': dataSource.conversationId, 'fromMailBoxId': dataSource.fromMailBoxId, 'toMailBoxId': dataSource.toMailBoxId };
  this.$actionHelper.callApi(action);
}

functions.getMailboxMessages:
async function(mailbox, dataSource, $dynamicUiExtraService, $actionHelper) {  $actionHelper.$loaderService.present().then(async (res) => {
    const messagesResponse = await $dynamicUiExtraService.getMailboxConversations(mailbox.lookupId);
    const messages = messagesResponse.messages;
    const cursor = messagesResponse.cursor; 
    dataSource.messages = messages;
    dataSource.cursor = cursor;
    dataSource.targetMailbox = mailbox;
    $dynamicUiExtraService.saveSelectedMailbox(mailbox.lookupId);
    $actionHelper.$loaderService.dismiss();
  });
}

functions.goToConversationMessages:
function(dataSource, item) {  item.totalUnreadMessages = 0 ;
  let receiverMailboxId;
  let targetType;
  let targetId;

  if(dataSource.targetMailbox.lookupId === item.receiverMailboxId) {
    receiverMailboxId = item.senderMailboxId;
    targetType = item.senderMailboxOwnerType;
    targetId = item.senderMailboxOwnerId;          
  } else {
    receiverMailboxId = item.receiverMailboxId;
    targetType = item.receiverMailboxOwnerType;
    targetId = item.receiverMailboxOwnerId;          
  }

  $actionHelper.navigateTo('conversationMessages/' + targetType +  '/'+ targetId + '/' + dataSource.targetMailbox.lookupId + '/' + receiverMailboxId)
}

