functions.getFullTree:
async function (dataSource) {  const parentChapterResponse = await $dynamicUiExtraService.getOrganizationParentChapters($activatedRoute.snapshot.params.id, $actionHelper);
  const rootChapterId = parentChapterResponse.rootChapterId;
  const hasParentChapter = parentChapterResponse.hasParentChapter;
  const parentChapterId = parentChapterResponse.parentChapterId;

  let chaptersTree = [];
  if(hasParentChapter) {
    chaptersTree = await $dynamicUiExtraService.prepareOrganizationChildChapters(rootChapterId, $actionHelper, $activatedRoute.snapshot.params.id);
  }
  dataSource.chaptersTree = chaptersTree;
  dataSource.parentChapterId = parentChapterId;  
}

functions.removeChapter:
function ($id,$chapterId, $dynamicUiExtraService, dataSource) {  this.$actionHelper.$loaderService.present().then(() => 
    this.getGraphQL(`
