        organization {
          removeParentEntity(id: $id, parentId: $chapterId)
        }
      }
    `, {id: $id, chapterId: $chapterId}).then((data) => {
        $actionHelper.showToast('Chapter link removed successfully');
        this.getFullTree(dataSource);


        this.getOutgoingRequest(dataSource);
        $actionHelper.notifiySubscribers(['manage-organization-chapters']); 
      }).finally(() => {
      this.$actionHelper.$loaderService.dismiss();
