functions.getMailboxMessages:
async function(mailbox, dataSource, $dynamicUiExtraService, $actionHelper) {  $actionHelper.$loaderService.present().then(async (res) => {
    const messagesResponse = await $dynamicUiExtraService.getMailboxConversations(mailbox.lookupId);
    const messages = messagesResponse.messages;
    const cursor = messagesResponse.cursor; 
    dataSource.messages = messages;
    dataSource.cursor = cursor;
    dataSource.targetMailbox = mailbox;
    $dynamicUiExtraService.saveSelectedMailbox(mailbox.lookupId);
    $actionHelper.$loaderService.dismiss();
  });
}

functions.goToConversationMessages:
function(dataSource, item) {  item.totalUnreadMessages = 0 ;
  let receiverMailboxId;
  let targetType;
  let targetId;

  if(dataSource.targetMailbox.lookupId === item.receiverMailboxId) {
    receiverMailboxId = item.senderMailboxId;
    targetType = item.senderMailboxOwnerType;
    targetId = item.senderMailboxOwnerId;          
  } else {
    receiverMailboxId = item.receiverMailboxId;
    targetType = item.receiverMailboxOwnerType;
    targetId = item.receiverMailboxOwnerId;          
  }

  $actionHelper.navigateTo('conversationMessages/' + targetType +  '/'+ targetId + '/' + dataSource.targetMailbox.lookupId + '/' + receiverMailboxId)
}

