      scopeKey: 'events'
    };


    let products = {
      title: 'Products',
      items:  _.get(data, "search.all.everything.items", []).filter(x => x.__typename === 'Offer' && x.type.toLowerCase() === 'product').map(x => $dynamicUiExtraService.mapCompanySearchOfferToItem(x)),
      domainRoute: 'Offer',
      cardType: 'offerCard',
      section: 'businesses',
      scopeKey: 'products'
    };

    let services = {
      title: 'Services',
      items:  _.get(data, "search.all.everything.items", []).filter(x => x.__typename === 'Offer' && x.type.toLowerCase() === 'service').map(x => $dynamicUiExtraService.mapCompanySearchOfferToItem(x)),
      domainRoute: 'Offer',
      cardType: 'offerCard',
      section: 'businesses',
      scopeKey: 'services'
    };

    let franchises = {
      title: 'Franchises',
      items:  _.get(data, "search.all.everything.items", []).filter(x => x.__typename === 'Offer' && x.type.toLowerCase() === 'franchise').map(x => $dynamicUiExtraService.mapCompanySearchOfferToItem(x)),
      domainRoute: 'Offer',
      cardType: 'offerCard',
      section: 'businesses',
      scopeKey: 'franchises'
    };

    let dataResult = [];
    dataResult.push(people);
    dataResult.push(company);
    dataResult.push(startup);
    dataResult.push(socialEnterprise);
    dataResult.push(hometowns);
    dataResult.push(organizations);
    dataResult.push(diplomaticMissions);
    dataResult.push(news);
    dataResult.push(events);
    dataResult.push(products);
    dataResult.push(services);
    dataResult.push(franchises);

    let everyThingDataSection = dataResult;
    let peopleDataSection = dataResult.filter(x => x.section === 'people');
    let businessesDataSection = dataResult.filter(x => x.section === 'businesses');
    let networksDataSection = dataResult.filter(x => x.section === 'networks');

    const everythingTotalItems = 
          people.items.length

        + company.items.length
        + startup.items.length
        + socialEnterprise.items.length
        + products.items.length
        + services.items.length
        + franchises.items.length

        + hometowns.items.length
        + organizations.items.length     
        + diplomaticMissions.items.length
        + news.items.length
        + events.items.length 

    const peopleTotalItems = people.items.length;
    const businessesTotalItems = 
        + company.items.length
        + startup.items.length
        + socialEnterprise.items.length
        + products.items.length
        + services.items.length
        + franchises.items.length

    const networksTotalItems = 
        + hometowns.items.length
        + organizations.items.length     
        + diplomaticMissions.items.length
        + news.items.length
        + events.items.length                     

    let everythingDataResult = {
      dataResult: everyThingDataSection,
      totalItems: everythingTotalItems,
      section: 'everything'
    }

    let peopleDataResult = {
      dataResult: peopleDataSection,
      totalItems: peopleTotalItems,
      section: 'people'
    }

    let businessesgDataResult = {
      dataResult: businessesDataSection,
      totalItems: businessesTotalItems,
      section: 'businesses'
    }

    let networksDataResult = {
      dataResult: networksDataSection,
      totalItems: networksTotalItems,
      section: 'networks'
    }

    result.dataResult.push(everythingDataResult);
    result.dataResult.push(peopleDataResult);
    result.dataResult.push(businessesgDataResult);
    result.dataResult.push(networksDataResult);

    $actionHelper.printData({ result, data })
    return result;
  }
)(data)

