functions.createPost:
function ($entityId, $title, $description, $photo) {  
  this.$actionHelper.$loaderService.present().then(() => 

  this.getGraphQL(`
      mutation($entityId: String!, $title: String!, $description: String!, $photo: Upload) {

        $dynamicUiExpression((|$activatedRoute.snapshot.params.type|)) {
          addPost(
            id: $entityId, 
            model: {
              title: $title,
              description: $description,

            },
            photo: $photo
          ) {
            id
          }
        }

      }
    `, {entityId: $entityId, title: $title, description: $description,  photo: $photo}).then(uploadResponse => {
        $actionHelper.showToast('Post added successfully');
        $actionHelper.notifiySubscribers(['add-post'], true);
    }).finally(() => {
      this.$actionHelper.$loaderService.dismiss();
    })   
  )
}

