functions.createEvent:
function ($entityId, $title, $description, $location, $startDate, $endDate, $photo) {    if(new Date($startDate).getTime() >= new Date($endDate).getTime()) {
        this.$actionHelper.translateService.get('EVENT.START_EVENT_DATE_VALIDATION').subscribe((message) => {
          this.$actionHelper.showToast(message)
        });
        return;
    }

  this.$actionHelper.$loaderService.present().then(() => 

    this.getGraphQL(`
      mutation($entityId: String!, $title: String!, $description: String!, $location: String!, $startDate: DateTime!, $endDate: DateTime!, $photo: Upload!) {

        $dynamicUiExpression((|$activatedRoute.snapshot.params.type|)) {
          addEvent(
            id: $entityId, 
            model: {
              title: $title,
              description: $description,
              location: $location,
              startDate: $startDate,
              endDate: $endDate
            },
            photo: $photo
          ) {
            id
          }
        }

      }
    `, {entityId: $entityId, title: $title, description: $description, location: $location, startDate: $startDate, endDate: $endDate,  photo: $photo}).then(uploadResponse => {
        $actionHelper.showToast('Post added successfully');
        $actionHelper.notifiySubscribers(['add-post'], true);
