    this.getGraphQL(`
      mutation($id: String!, $caption: String, $photo: Upload!) {
        mediaGallery {
          uploadImage(id: $id, caption: $caption, photo: $photo) {
            id
          }
        }
      }
    `, {id: $id, caption: $newCaption, photo: $event}).then(uploadResponse => {
        $actionHelper.showToast('Photo added successfully');
        $actionHelper.notifiySubscribers(['add-media'], true);
    }).finally(() => {
      this.$actionHelper.$loaderService.dismiss();
    })   
  )
}

