functions.checkYoutubeLink:
function(youTubeUrl, imageId) {  if (youTubeUrl !== undefined || youTubeUrl !== '') {
    const regExp = /^.*((youtu.be\/)|(v\/)|(\/u\/\w\/)|(embed\/)|(watch\?))\??v?=?([^#&?]*).*/;
    const match = youTubeUrl.match(regExp);
    if (match && match[7].length == 11) {
      const youtubeId = match[7];
      if(youtubeId) {
        const url = `https://www.googleapis.com/youtube/v3/videos?part=id%2C+snippet&id=${youtubeId}&key=AIzaSyAWLAzXtjHPDdyVmZqxFKqqw2LjmDapr68`
        const params = {'url': url}
        this.$actionHelper.$loaderService.present().then(() => {
          this.$actionHelper.advancedHttp.get(url, {}, {}).then(response => {
            if (response.status === 200) {
                const data = response.data ? JSON.parse(response.data): null;
                const items = _.get(data, "items");
                if(items && items.length > 0) {
                  const videoItem = items[0];
                  const videoDescription = _.get(videoItem, "snippet.description");
                  const videoImage = _.get(videoItem, "snippet.thumbnails.high.url");

                  $actionHelper.setFormGroupData('Image', videoImage);
                  $actionHelper.setFormGroupData('caption', videoDescription);
                  $actionHelper.setYoutubeVideoLink(youTubeUrl);
                  document.getElementById(imageId).src = videoImage;
                } else {
                  this.$actionHelper.showToast('Invalid YouTube URL');
                }
            } else throw response;
          })
          .catch(error => {
            $actionHelper.printData(JSON.stringify(error))
          })
          .finally(() => {
            this.$actionHelper.$loaderService.dismiss();
          });  
        })  
      } else {
        this.$actionHelper.showToast('Invalid YouTube URL');
      }
    } else {
      this.$actionHelper.showToast('Invalid YouTube URL');
    }
  }      
}

