function(activity, commentText, comments) {  let action =  {};
  const newCommentText = commentText.replace(/\n/g, '<br>');
  if(activity.cardType === 'news') {
    action.query = "mutation { post { addComment(id: $templateContext->additionalParameters.entityId, text: $templateContext->additionalParameters.text) { ...commentProperties } }}"
    action.postAction = "const comment = $dynamicUiExtraService.mapNewCommentToItem(templateContext.additionalParameters.activity, $graphQlResults.post.addComment); templateContext.additionalParameters.comments.push(comment); templateContext.additionalParameters.$actionHelper.scrollToBotton('cardDetailsDiv')"

  } else if(activity.cardType === 'event') {
    action.query = "mutation { event { addComment(id: $templateContext->additionalParameters.entityId, text: $templateContext->additionalParameters.text) { ...commentProperties } }}"
    action.postAction = "const comment = $dynamicUiExtraService.mapNewCommentToItem(templateContext.additionalParameters.activity, $graphQlResults.event.addComment); templateContext.additionalParameters.comments.push(comment); templateContext.additionalParameters.$actionHelper.scrollToBotton('cardDetailsDiv')"
  }

  action.additionalParameters = {'activity': activity, 'entityId':activity.id, 'text': newCommentText, 'comments': comments, '$actionHelper': this.$actionHelper  };
  this.$actionHelper.callApi(action);
}

