functions.afterInit:
function () {  $actionHelper.$notificationService.registerTokenDevice();
  $actionHelper.$messagingService.getUnreadMessageCount();
  $actionHelper.prepareLookups();
  if($actionHelper.$parametersStorageService.checkIfDeeplinkOrNotificationResponse()) {
    const parameters = $actionHelper.$parametersStorageService.getDeeplinkOrNotificationResponse();
    $actionHelper.goToTargetFromNotificationOrDeeplink(parameters, this.initialDataSource.profileId);
  }

  $actionHelper.initilizeNotificationAndDeeplinkSubscribers();
  $actionHelper.deeplinkAndNotificationSubject.subscribe((data) => {
    if($actionHelper.$authenticationService.isLoggedIn) {
      if($actionHelper.$parametersStorageService.checkIfDeeplinkOrNotificationResponse()) {
        const parameters = $actionHelper.$parametersStorageService.getDeeplinkOrNotificationResponse();
        $actionHelper.goToTargetFromNotificationOrDeeplink(parameters, this.initialDataSource.profileId);
      }
    }
  });

  if($actionHelper.followSubject) {
    this.followSubscription = $actionHelper.followSubject.subscribe((followInfo) => {
      if(followInfo) {
        $actionHelper.checkFollowList(this.dataSource.peoples, followInfo);
        $actionHelper.checkFollowList(this.dataSource.organizations, followInfo);
        $actionHelper.checkFollowList(this.dataSource.businesses, followInfo);
