  }

  const parentBranchesResponse = await $dynamicUiExtraService.getCompanyParentBranches($activatedRoute.snapshot.params.id, $actionHelper);
  const rootBranchId = parentBranchesResponse.rootBranchId;
  const hasParentBranch = parentBranchesResponse.hasParentBranch;
  const parentBranchId = parentBranchesResponse.parentBranchId;

  let branchesTree = [];
  if(hasParentBranch) {
    branchesTree = await $dynamicUiExtraService.prepareCompanyChildBranches(rootBranchId, $actionHelper, $activatedRoute.snapshot.params.id);
  }

  result.branchesTree = branchesTree;
  result.parentBranchId = parentBranchId;

  $actionHelper.printData({ result, data })
  return result;
}

)(data)

