dataSource.dataExpression:
(async (data) => {
  const fromMailBoxId = $activatedRoute.snapshot.params.senderMailboxId;
  const toMailBoxId =  $activatedRoute.snapshot.params.receiverMailboxId;

  const conversationId = _.get(data, 'conversation.getConversation.id', null)
  const conversationDetails = await $dynamicUiExtraService.getConversationDetails(conversationId);
  const mailboxName = $dynamicUiExtraService.getConversationDetailsSenderMailboxId(conversationDetails.conversation) === fromMailBoxId ? 
                        $dynamicUiExtraService.getConversationDetailsReceiverTypeMailbox(conversationDetails.conversation) === 'User' ? ($dynamicUiExtraService.getConversationDetailsReceiverFirstNameMailbox(conversationDetails.conversation) + ' ' + $dynamicUiExtraService.getConversationDetailsReceiverLastNameMailbox(conversationDetails.conversation)) : ($dynamicUiExtraService.getConversationDetailsReceiverNameMailbox(conversationDetails.conversation)):
                        $dynamicUiExtraService.getConversationDetailsSenderTypeMailbox(conversationDetails.conversation) === 'User' ? ($dynamicUiExtraService.getConversationDetailsSenderFirstNameMailbox(conversationDetails.conversation) + ' ' + $dynamicUiExtraService.getConversationDetailsSenderLastNameMailbox(conversationDetails.conversation)) : ($dynamicUiExtraService.getConversationDetailsSenderNameMailbox(conversationDetails.conversation));

  const conversationMessages =  _.get(conversationDetails, 'conversation.messages.items', []).map(x => $dynamicUiExtraService.mapToConversationMessages(x, fromMailBoxId));
  const messages = conversationMessages.reverse();

  const senderId =  $dynamicUiExtraService.getConversationDetailsSenderMailboxId(conversationDetails.conversation) === fromMailBoxId ? $dynamicUiExtraService.getConversationDetailsSenderIdMailbox(conversationDetails.conversation) : $dynamicUiExtraService.getConversationDetailsReceiverIdMailbox(conversationDetails.conversation);
  const senderName = $dynamicUiExtraService.getConversationDetailsSenderMailboxId(conversationDetails.conversation) === fromMailBoxId ? ($dynamicUiExtraService.getConversationDetailsSenderTypeMailbox(conversationDetails.conversation) === 'User' ? ($dynamicUiExtraService.getConversationDetailsSenderFirstNameMailbox(conversationDetails.conversation) + ' ' + $dynamicUiExtraService.getConversationDetailsSenderLastNameMailbox(conversationDetails.conversation)) : ($dynamicUiExtraService.getConversationDetailsSenderNameMailbox(conversationDetails.conversation))): 
                                                                                      ($dynamicUiExtraService.getConversationDetailsReceiverTypeMailbox(conversationDetails.conversation) === 'User' ? ($dynamicUiExtraService.getConversationDetailsReceiverFirstNameMailbox(conversationDetails.conversation) + ' ' + $dynamicUiExtraService.getConversationDetailsReceiverLastNameMailbox(conversationDetails.conversation)) : ($dynamicUiExtraService.getConversationDetailsReceiverNameMailbox(conversationDetails.conversation)));         
  const senderImage =  $dynamicUiExtraService.getConversationDetailsSenderMailboxId(conversationDetails.conversation) === fromMailBoxId ? $dynamicUiExtraService.getConversationDetailsSenderImageMailbox(conversationDetails.conversation) : $dynamicUiExtraService.getConversationDetailsReceiverImageMailbox(conversationDetails.conversation);

  $dynamicUiExtraService.setMessagesAsRead($actionHelper.getGraphQL, conversationId);

  const result =  {
    messages: messages,
    cursor:  _.get(conversationDetails, 'conversation.messages.cursor', null),
    mailboxName: mailboxName,
    conversationId: conversationId,
    fromMailBoxId: fromMailBoxId,
    toMailBoxId: toMailBoxId,
    senderId: senderId,
    senderName: senderName,
    senderImage: senderImage
  }

  $actionHelper.printData({ result, conversationDetails })
  return result;
}   
)(data)

