dataSource.dataExpression:
(async(data) => {
  const result =  {
    ...data,
    incomingChaptersRequests: _.get(data, "organization.incomingChaptersRequests.items", []).map(x => $dynamicUiExtraService.mapOrganizationToItem(x)),
    outgoingChaptersRequests: _.get(data, "organization.outgoingChaptersRequests.items",[]).map(x => $dynamicUiExtraService.mapOrganizationToItem(x)),
    incomingChaptersRequestsCursor: _.get(data, "organization.incomingChaptersRequests.cursor"),
    outgoingChaptersRequestsCursor: _.get(data, "organization.outgoingChaptersRequests.cursor"),
    actions: _.get(data, "organization.actions", {})
  }

  const parentChapterResponse = await $dynamicUiExtraService.getOrganizationParentChapters($activatedRoute.snapshot.params.id, $actionHelper);
  const rootChapterId = parentChapterResponse.rootChapterId;
  const hasParentChapter = parentChapterResponse.hasParentChapter;
  const parentChapterId = parentChapterResponse.parentChapterId;

  let chaptersTree = [];
  if(hasParentChapter) {
    chaptersTree = await $dynamicUiExtraService.prepareOrganizationChildChapters(rootChapterId, $actionHelper, $activatedRoute.snapshot.params.id);
  }

  result.chaptersTree = chaptersTree;
  result.parentChapterId = parentChapterId;

  $actionHelper.printData({ result, data })
  return result;
}
)(data)

