dataSource.dataExpression:
((data) => {
  const canCreateRoleAction = $dynamicUiExtraService.checkIfCanCreateRole(data.organization); 

  const canManage = $dynamicUiExtraService.checkIfCanManage(data.organization); 
  const canInvite = $dynamicUiExtraService.checkIfCanInvite(data.organization); 
  const canShare = $dynamicUiExtraService.checkIfCanShare(data.organization); 
  const canFollow = $dynamicUiExtraService.checkIfCanFollow(data.organization); 
  const canUnFollow = $dynamicUiExtraService.checkIfCanUnFollow(data.organization); 

  const canSendMessage = $dynamicUiExtraService.checkIfCanSendMessage(data.organization); 
  const canAddPost = $dynamicUiExtraService.checkIfCanAddPost(data.organization); 
  const canAddEvent = $dynamicUiExtraService.checkIfCanAddEvent(data.organization);  
  const canAddMember = $dynamicUiExtraService.checkIfCanAddMember(data.organization);   
  const canJoin = $dynamicUiExtraService.checkIfCanJoinEntity(data.organization);   

  const badges = $dynamicUiExtraService.prepareOrganizationBadges(data.organization, canManage);

  const generalDetailsInfo = $dynamicUiExtraService.prepareOrganizationInfo(data.organization, canManage); 
  const socialMediaInfo = $dynamicUiExtraService.prepareSocialMediaInfo(data.organization); 
  const galleryId = $dynamicUiExtraService.getGalleryId(data.organization);  

  const canUploadImage = $dynamicUiExtraService.checkIfCanUploadImage(data.organization);  
  const canUploadVideoLink = $dynamicUiExtraService.checkIfCanUploadVideoLink(data.organization); 
  const canUploadMedia = canUploadImage || canUploadVideoLink;

  const parentOrganization = _.get(data, "organization.parent", []).length > 0 ? _.get(data, "organization.parent")[0] : null;
  const prepareChildChapters = $dynamicUiExtraService.prepareChildChapters(data.organization);
  const chapterList = $dynamicUiExtraService.prepareTreeForOrganizationPage(parentOrganization, prepareChildChapters);
  const admins = _.get(data, "organization.admins.items", []).map(x => $dynamicUiExtraService.mapUserToItem(x));

  const result =  {
    ...data,
    members: _.get(data, "organization.employees.items", []).map(x => $dynamicUiExtraService.mapUserWithRoleToItem(x)),
    leaderShips: _.get(data, "organization.leaderships", []).map(x => $dynamicUiExtraService.mapUserWithRoleToItem(x)),
    chapters: chapterList ? chapterList.map(x => $dynamicUiExtraService.mapOrganizationToTreeItem(x)) : [],
    currentAffiliations: _.get(data, "organization.currentAffiliations.items", []).map(x => $dynamicUiExtraService.mapCompanyToItem(x)),
    committees: _.get(data, "organization.committees", []).map(x => $dynamicUiExtraService.mapCommitteeToItem(x)),
    gallery: _.get(data, "organization.gallery.mediaGallery.galleryItems.items", []).map(x => $dynamicUiExtraService.mapGalleryToItem(x)),
    updates: _.get(data, "organization.updates.items", []).map(x => $dynamicUiExtraService.mapUpdateToItem(x)),
    receiverMailboxId: _.get(data, "organization.mailBox.mailBox.id", null),
    admins: admins,

    generalDetailsInfo: generalDetailsInfo,
    socialMediaInfo: socialMediaInfo,
    galleryId: galleryId,
    badges: badges,

    canCreateRoleAction: canCreateRoleAction,
    canAddMember: canAddMember,
    canInvite: canInvite,
    canManage: canManage,
    canShare: canShare,
    canFollow: canFollow,
    canUnFollow: canUnFollow,
    canSendMessage: canSendMessage,
    canAddPost: canAddPost,
    canAddEvent: canAddEvent,
    canUploadMedia: canUploadMedia,
    canUploadImage: canUploadImage,
    canUploadVideoLink: canUploadVideoLink,
    canJoin: canJoin
  }
  $actionHelper.printData({ result, data })
  return result;
}   
)(data)

