dataSource.dataExpression:
((data) => {

  const canManage = $dynamicUiExtraService.checkIfUserCanManage(data); 
  const canAddPost = $dynamicUiExtraService.checkIfUserCanAddPost(data); 

  const canFollow = $dynamicUiExtraService.checkIfCanFollow(data.user); 
  const canUnFollow = $dynamicUiExtraService.checkIfCanUnFollow(data.user);  
  const canShare = $dynamicUiExtraService.checkIfCanShare(data.user); 
  const canInvite = $dynamicUiExtraService.checkIfCanInvite(data.user);  
  const canSendMessage = $dynamicUiExtraService.checkIfCanSendMessage(data.user); 

  const myProfile = ($activatedRoute.snapshot.params.id === initialDataSource.profileId) ? true: false;

  const generalDetailsInfo = $dynamicUiExtraService.prepareUserInfo(data.user, myProfile && canManage); 
  const socialMediaInfo = $dynamicUiExtraService.prepareSocialMediaInfo(data.user); 
  const userMailbox = _.get(data, "user.mailBoxes", []).find(x => x.mailBox.owner.entity.__typename === 'User');

  const galleryId = $dynamicUiExtraService.getGalleryId(data.user);  
  const badges = $dynamicUiExtraService.prepareUserBadges(data.user, myProfile && canManage);

  const canUploadImage =  $dynamicUiExtraService.checkIfCanUploadImage(data.user);  
  const canUploadVideoLink = $dynamicUiExtraService.checkIfCanUploadVideoLink(data.user); 
  const canUploadMedia = canUploadImage || canUploadVideoLink;
  const hasTownRole = _.get(data, 'user.roles', []).filter(userRole => (((_.get(userRole, 'entity.__typename', '') || '').toLowerCase() === 'municipality') && ((_.get(userRole, 'role.role', '') || '').toLowerCase() === 'electedmember'))).length > 0 ? true : false; 
  const hasDiplomaticRole = _.get(data, 'user.networkRoles', []).filter(userRole => (_.get(userRole, 'entity.__typename', '').toLowerCase() === 'diplomaticmission') && (_.get(userRole, 'role.role', '').toLowerCase() === 'president')).length > 0 ? true : false; 

  const result =  {
    ...data,
    galleryId: galleryId,
    hasDiplomaticRole: hasDiplomaticRole,
    hasTownRole: hasTownRole,
    businessRoles: _.get(data, "user.businessRoles", []).map(x => $dynamicUiExtraService.mapBusinessRoleToItem(x)),
    networkRoles: _.get(data, "user.networkRoles", []).map(x => $dynamicUiExtraService.mapNetworRoleToItem(x)),
    peoples: _.get(data, "user.people.items", []).map(x => $dynamicUiExtraService.mapUserToItem(x)),
    gallery: _.get(data, "user.gallery.mediaGallery.galleryItems.items", []).map(x => $dynamicUiExtraService.mapGalleryToItem(x)),
    updates: _.get(data, "user.updates.items", []).map(x => $dynamicUiExtraService.mapPostToItem(x)),
    receiverMailboxId: _.get(userMailbox, "mailBox.id", null),

    generalDetailsInfo: generalDetailsInfo,
    socialMediaInfo: socialMediaInfo,
    badges: badges,

    canManage: canManage,
    canAddPost: canAddPost,
    canUploadMedia: canUploadMedia,
    canUploadImage: canUploadImage,
    canUploadVideoLink: canUploadVideoLink,            

    canFollow: canFollow,
    canUnFollow: canUnFollow,
    canShare: canShare,
    canInvite: canInvite,
    canSendMessage: canSendMessage,

    myProfile: myProfile
  }
  $actionHelper.printData({ result, data })
  return result;
}   
)(data)

