dataSource.dataExpression:
(async (data) => {
    let selectedMailbox;
    const showBadge = false;

    const mailboxes = _.get(data, "user.mailBoxes", []).map(x => $dynamicUiExtraService.mapMailboxToItem(x));
    if($dynamicUiExtraService.getSelectedMailbox()) {
      selectedMailbox = mailboxes.find(mailbox => mailbox.lookupId === $dynamicUiExtraService.getSelectedMailbox());
      if(!selectedMailbox) {
        selectedMailbox = mailboxes.find(mailbox => mailbox.type === 'User');
      }
    } else {
      selectedMailbox = mailboxes.find(mailbox => mailbox.type === 'User');
    }

    const messagesResponse = await $dynamicUiExtraService.getMailboxConversations(selectedMailbox.lookupId);
    const messages = messagesResponse.messages;
    const cursor = messagesResponse.cursor; 

    const result =  {
      mailboxes: mailboxes,
      messages: messages,
      targetMailbox: selectedMailbox,
      showBadge: showBadge,
      cursor: cursor
    }

    $actionHelper.printData({ result, data })
    return result;

  }
)(data)

