$actionHelper.updateLinkToDomain:
function(domainType, domainId, item, updateType) {  domainType = _.camelCase(domainType);
  domainType = (domainType === 'profile') ? 'user' : domainType;
  this.$loaderService.present().then(() => {
    item.isUpdatingLinkToDomain = true;
    const that = this;
    this.getGraphQL(`mutation   { 
    ${domainType} {
      ${updateType}(id: "${domainId}")
    }
    }`).then(
      (data) => {
        item.canFollow = !item.canFollow;
        item.canUnFollow = !item.canUnFollow;
        this.followSubject.next({domainId: domainId, domainType: domainType, canFollow: item.canFollow, canUnFollow: item.canUnFollow })
      }
    ).finally(() => {
      this.$loaderService.dismiss();
      item.isUpdatingLinkToDomain = false;
    })
  });
}

$actionHelper.getMethodFromDeeplinkg:
function(url, deeplinkResponse) {  this.printData({url, deeplinkResponse});
  if (url === undefined || url === null) {
    return;
  } else {
    if (url.includes(this.DeepLinkingURL.DIASPORA_PROFILE_URL)) {
      const targetId = this.generalService.getParameterFromUrl(url, 'ProfileId');
      if (targetId && targetId.length > 0) {
        deeplinkResponse.targetId = targetId;
        deeplinkResponse.calledMethod = this.DeepLinkingMethods.DiasporaProfile;
        deeplinkResponse.url = url;              
        this.$parametersStorageService.saveDeeplinkOrNotificationResponse(deeplinkResponse);
        this.notifyDeeplinkAndNotificationSubscribers(deeplinkResponse);
      } 
    } else if (url.includes(this.DeepLinkingURL.DIASPORA_MUNICIPALITY_URL)) {
      const targetId = this.generalService.getParameterFromUrl(url, 'MunicipalityId');

      if (targetId && targetId.length > 0) {
        deeplinkResponse.targetId = targetId;
        deeplinkResponse.calledMethod = this.DeepLinkingMethods.DiasporaMunicipality;
        deeplinkResponse.url = url;              
        this.$parametersStorageService.saveDeeplinkOrNotificationResponse(deeplinkResponse);
        this.notifyDeeplinkAndNotificationSubscribers(deeplinkResponse);
      } 
    }
    else if (url.includes(this.DeepLinkingURL.DIASPORA_COMPANY_URL)) {
      const targetId = this.generalService.getParameterFromUrl(url, 'CompanyId');

      if (targetId && targetId.length > 0) {
        deeplinkResponse.targetId = targetId;
        deeplinkResponse.calledMethod = this.DeepLinkingMethods.DiasporaCompany;
        deeplinkResponse.url = url;              
        this.$parametersStorageService.saveDeeplinkOrNotificationResponse(deeplinkResponse);
        this.notifyDeeplinkAndNotificationSubscribers(deeplinkResponse);
      }
    }
    else if (url.includes(this.DeepLinkingURL.DIASPORA_ORGANIZATION_URL)) {
      const targetId = this.generalService.getParameterFromUrl(url, 'OrganizationId');

      if (targetId && targetId.length > 0) {
        deeplinkResponse.targetId = targetId;
        deeplinkResponse.calledMethod = this.DeepLinkingMethods.DiasporaOrganization;
        deeplinkResponse.url = url;              
        this.$parametersStorageService.saveDeeplinkOrNotificationResponse(deeplinkResponse);
        this.notifyDeeplinkAndNotificationSubscribers(deeplinkResponse);
      } 
    }
    else if (url.includes(this.DeepLinkingURL.DIASPORA_EMBASSY_URL)) {
      const targetId = this.generalService.getParameterFromUrl(url, 'EmbassyId');

      if (targetId && targetId.length > 0) {
        deeplinkResponse.targetId = targetId;
        deeplinkResponse.calledMethod = this.DeepLinkingMethods.DiasporaDiplomatic;
        deeplinkResponse.url = url;              
        this.$parametersStorageService.saveDeeplinkOrNotificationResponse(deeplinkResponse);
        this.notifyDeeplinkAndNotificationSubscribers(deeplinkResponse);
      } 
    }
    else if (url.includes(this.DeepLinkingURL.DIASPORA_ACTIVITY_URL)) {
      const targetId = this.generalService.getParameterFromUrl(url, 'EntityId');
      const targetType = this.generalService.getParameterFromUrl(url, 'EntityType');

      if (targetId && targetId.length > 0 && targetType && targetType.length > 0) {
        deeplinkResponse.targetId = targetId;
        deeplinkResponse.calledMethod = (targetType.toLowerCase() === 'offer') ? this.DeepLinkingMethods.DiasporaOfferDetails : this.DeepLinkingMethods.DisplayActivityDetails;
        deeplinkResponse.cardType = targetType.toLowerCase();
        deeplinkResponse.url = url;              
        this.$parametersStorageService.saveDeeplinkOrNotificationResponse(deeplinkResponse);
        this.notifyDeeplinkAndNotificationSubscribers(deeplinkResponse);
      }
    }
    else if (url.includes(this.DeepLinkingURL.DIASPORA_ABOUT_URL)) {
      deeplinkResponse.calledMethod = this.DeepLinkingMethods.DiasporaAbout;
      deeplinkResponse.url = url;              
      this.$parametersStorageService.saveDeeplinkOrNotificationResponse(deeplinkResponse);
      this.notifyDeeplinkAndNotificationSubscribers(deeplinkResponse);
    }
    else if (url.includes(this.DeepLinkingURL.DIASPORA_NOTIFICATION_SETTINGS_URL)) {
      deeplinkResponse.calledMethod = this.DeepLinkingMethods.DiasporaNotificationSettings;
      deeplinkResponse.url = url;              
      this.$parametersStorageService.saveDeeplinkOrNotificationResponse(deeplinkResponse);
      this.notifyDeeplinkAndNotificationSubscribers(deeplinkResponse);            
    }
    else if (url.includes(this.DeepLinkingURL.DIASPORA_NOTIFICATION_URL)) {
      deeplinkResponse.calledMethod = this.DeepLinkingMethods.DiasporaNotification;
      deeplinkResponse.url = url;              

      this.$parametersStorageService.saveDeeplinkOrNotificationResponse(deeplinkResponse);
      this.notifyDeeplinkAndNotificationSubscribers(deeplinkResponse);
    } else {
      switch (deeplinkResponse.calledMethod) {

        case this.DeepLinkingMethods.DiasporaMunicipality:
        case this.DeepLinkingMethods.DisplayActivityDetails:
        case this.DeepLinkingMethods.DiasporaOfferDetails:
        case this.DeepLinkingMethods.DiasporaProfile:
        case this.DeepLinkingMethods.DiasporaCompany:
        case this.DeepLinkingMethods.DiasporaDiplomatic:
        case this.DeepLinkingMethods.DiasporaOrganization:
        case this.DeepLinkingMethods.DiasporaAdminMunicipality:
        case this.DeepLinkingMethods.DiasporaAdminCompany:
        case this.DeepLinkingMethods.DiasporaAdminDiplomatic:
        case this.DeepLinkingMethods.DiasporaAdminOrganization:
        case this.DeepLinkingMethods.DiasporaStaffMember:
        case this.DeepLinkingMethods.DiasporaCommitteeMember:
          deeplinkResponse.url = url;              
          this.$parametersStorageService.saveDeeplinkOrNotificationResponse(deeplinkResponse);
          this.notifyDeeplinkAndNotificationSubscribers(deeplinkResponse);
        break;

        case this.DeepLinkingMethods.InviteToTown: 
          deeplinkResponse.url = url;              
          this.$parametersStorageService.saveDeeplinkOrNotificationResponse(deeplinkResponse);
        break;
      }
    }
  }
}

$actionHelper.getMethodFromNotification:
function(notificationPayload) {  const notificationResponse = new this.DeeplinkResponse();
  switch(notificationPayload.targetType.toLowerCase()) {
    case 'user': 
      notificationResponse.calledMethod = this.DeepLinkingMethods.DiasporaProfile;          
    break;

    case 'company': 
      notificationResponse.calledMethod = this.DeepLinkingMethods.DiasporaCompany;          
    break;

    case 'organization': 
      notificationResponse.calledMethod = this.DeepLinkingMethods.DiasporaOrganization;          
    break;

    case 'diplomaticmission': 
      notificationResponse.calledMethod = this.DeepLinkingMethods.DiasporaDiplomatic;          
    break;

    case 'municipality': 
      notificationResponse.calledMethod = this.DeepLinkingMethods.DiasporaMunicipality;          
    break;

    case 'post': 
      notificationResponse.calledMethod = this.DeepLinkingMethods.DisplayActivityDetails;          
      notificationResponse.cardType = 'post';
    break;

    case 'event': 
      notificationResponse.calledMethod = this.DeepLinkingMethods.DisplayActivityDetails;    
      notificationResponse.cardType = 'event';
    break;


    case 'offer': 
      notificationResponse.calledMethod = this.DeepLinkingMethods.DiasporaOfferDetails;          
    break;          

    case 'conversation': 
      notificationResponse.calledMethod = this.DeepLinkingMethods.DiasporaConversationDetails;
    break;

    case 'comment': 
      notificationResponse.calledMethod = this.DeepLinkingMethods.DiasporaCommentDetails;
    break;

  }
  notificationResponse.targetId = notificationPayload.targetId;
  this.$parametersStorageService.saveDeeplinkOrNotificationResponse(notificationResponse);
  this.notifyDeeplinkAndNotificationSubscribers(notificationResponse);
}

$actionHelper.goToTargetFromNotificationOrDeeplink:
function(targetDetails, userId) {  switch (targetDetails.calledMethod) {
    case this.DeepLinkingMethods.DiasporaAbout:
      this.ngZone.run(() => {
        this.navigateTo('aboutUs');
      });
    break;

    case this.DeepLinkingMethods.DiasporaNotification:
      this.ngZone.run(() => {
        this.navigateTo('notifications');
      });
    break;

    case this.DeepLinkingMethods.DiasporaNotificationSettings:
      this.ngZone.run(() => {
        this.navigateTo('manage/notifications');
      });
    break;

    case this.DeepLinkingMethods.DiasporaProfile:
      this.ngZone.run(() => {
        this.navigateTo('profile/' + targetDetails.targetId);
      });
    break;

    case this.DeepLinkingMethods.DiasporaCompany:
      this.ngZone.run(() => {
        this.navigateTo('company/' + targetDetails.targetId);
      });
    break;

    case this.DeepLinkingMethods.DiasporaDiplomatic:
      this.ngZone.run(() => {
        this.navigateTo('diplomaticMission/' + targetDetails.targetId);
      });
    break;

    case this.DeepLinkingMethods.DiasporaMunicipality:
      this.ngZone.run(() => {
        this.navigateTo('town/' + targetDetails.targetId);
      });
    break;

    case this.DeepLinkingMethods.DiasporaOrganization:
      this.ngZone.run(() => {
        this.navigateTo('organization/' + targetDetails.targetId);
      });
    break;

    case this.DeepLinkingMethods.DisplayActivityDetails:
      this.ngZone.run(() => {
        targetDetails.cardType === 'event' ? this.navigateTo('eventDetails/' + targetDetails.targetId) : this.navigateTo('postDetails/' + targetDetails.targetId);
      });
    break;

    case this.DeepLinkingMethods.DiasporaOfferDetails: 
      this.ngZone.run(() => {
        this.navigateTo('offerDetails/' + targetDetails.targetId);
      });
    break;

    case this.DeepLinkingMethods.DiasporaMessageAccount:
      this.ngZone.run(() => {
        this.navigateTo('tabs/mailboxConversations');
      });
    break;

    case this.DeepLinkingMethods.CompanyAffiliation:
      this.ngZone.run(() => {
        this.navigateTo('company/' + targetDetails.targetId);
      });
    break;

    case this.DeepLinkingMethods.OrganizationAffliation:
      this.ngZone.run(() => {
        this.navigateTo('organization/' + targetDetails.targetId);
      });
    break;

    case this.DeepLinkingMethods.ManageCompanyMembers:
      this.ngZone.run(() => {
        this.navigateTo('company/manageCompanyTeamMembers/' + targetDetails.targetId);
      });
    break;

    case this.DeepLinkingMethods.ManageOrganizationMembers:
      this.ngZone.run(() => {
        this.navigateTo('organization/manageOrganizationMembers/' + targetDetails.targetId);
      });
    break;

    case this.DeepLinkingMethods.ManageDiplomaticMembers:
      this.ngZone.run(() => {
        this.navigateTo('diplomaticMission/manageDiplomaticKeyStaff/' + targetDetails.targetId);
      });
    break;

    case this.DeepLinkingMethods.DiasporaManageCompanyBranches:
      this.ngZone.run(() => {
        this.navigateTo('company/manageCompanyBranches/' + targetDetails.targetId);
      });
    break;

    case this.DeepLinkingMethods.DiasporaManageOrganizationChapters:
      this.ngZone.run(() => {
        this.navigateTo('organization/manageOrganizationChapters/' + targetDetails.targetId);
      });
    break;

    case this.DeepLinkingMethods.DiasporaConversationDetails:
      this.goToConversation(targetDetails.targetId);
    break;

    case this.DeepLinkingMethods.DiasporaCommentDetails: 
      this.goToCommentDetails(targetDetails.targetId);
    break;

    case this.DeepLinkingMethods.DiasporaAdminMunicipality:
      case this.DeepLinkingMethods.DiasporaAdminCompany:
      case this.DeepLinkingMethods.DiasporaAdminDiplomatic:
      case this.DeepLinkingMethods.DiasporaAdminOrganization:
      case this.DeepLinkingMethods.DiasporaStaffMember:
      case this.DeepLinkingMethods.DiasporaCommitteeMember:
        setTimeout(() => {
          this.checkIfFromInvitation(targetDetails, userId);
        }, 1500);              
    break;  
  }
  this.$parametersStorageService.clearDeeplinkOrNotificationResponse();
}

$actionHelper.goToCommentDetails:
function(targetId) {  let action = {};
  action.query = "query { comment (id:$templateContext->additionalParameters.targetId) { id text listUnder { entity {  __typename ... on Event { id } __typename ... on Post { id } __typename ... on Offer { id } } } } }"
  action.postAction = "const commentDetails = $graphQlResults.comment; const commentActivityType = _.get(commentDetails, 'listUnder.entity.__typename', null); const commentActivityId = _.get(commentDetails, 'listUnder.entity.id', null); $actionHelper.navigateToDomain(commentActivityType, commentActivityId);"
  action.additionalParameters = {'targetId': targetId };
  this.callApi(action);
}

$actionHelper.goToConversation:
function(targetId) {  let action = {};
  action.query = "query { conversation (id:$templateContext->additionalParameters.targetId) { id fromMailBoxId toMailBoxId receiver { id owner { entity { __typename ... on User { id firstName lastName photoCover { imageThumbnailUrl imageUrl } } __typename ... on Company { id name photoCover { imageThumbnailUrl imageUrl } } __typename ... on Organization { id name photoCover { imageThumbnailUrl imageUrl } } __typename ... on DiplomaticMission { id name photoCover { imageThumbnailUrl imageUrl } } __typename ... on Municipality { id name: title photoCover { imageThumbnailUrl imageUrl } } } } } sender { id owner { entity { __typename ... on User {id firstName  lastName photoCover { imageThumbnailUrl imageUrl } } __typename ... on Company { id name photoCover { imageThumbnailUrl imageUrl } } __typename ... on Organization { id  name photoCover { imageThumbnailUrl imageUrl } } __typename ... on DiplomaticMission { id name photoCover { imageThumbnailUrl imageUrl } } __typename ... on Municipality { id name: title photoCover { imageThumbnailUrl imageUrl } } } } } } }"
  action.postAction = "const conversation = $graphQlResults.conversation; const conversationType = _.get(conversation, 'receiver.owner.entity.__typename', null); const conversationTargetId = _.get(conversation, 'receiver.owner.entity.id', null); const conversationSenderMailboxId = _.get(conversation, 'fromMailBoxId', null); const conversationReceiverMailboxId = _.get(conversation, 'toMailBoxId', null); $actionHelper.navigateTo('conversationMessages/' + conversationType +  '/'+ conversationTargetId + '/' + conversationSenderMailboxId + '/' + conversationReceiverMailboxId);"
  action.additionalParameters = {'targetId': targetId };
  this.callApi(action);
}

$actionHelper.checkIfFromInvitation:
function(deeplinkResponse, userId) {  const urlParams = this.getAllUrlParams(deeplinkResponse.url);

  const targetId = deeplinkResponse.targetId;
  const committeeId = deeplinkResponse.committeeId;
  const invitationId = urlParams.invitationid;

  switch (deeplinkResponse.calledMethod) {
    case this.DeepLinkingMethods.DiasporaAdminMunicipality:
      this.assignUserAsMunicipalityAdmin(targetId, invitationId, userId);
    break;

    case this.DeepLinkingMethods.DiasporaAdminCompany:
      this.assignUserAsCompanyAdmin(targetId, invitationId, userId);
    break;

    case this.DeepLinkingMethods.DiasporaAdminDiplomatic:
      this.assignUserAsDiplomaticMissionAdmin(targetId, invitationId, userId);
    break;

    case this.DeepLinkingMethods.DiasporaAdminOrganization:
      this.assignUserAsOrganizationAdmin(targetId, invitationId, userId);
    break;

    case this.DeepLinkingMethods.DiasporaStaffMember:
      this.assignUserAsStaffMember(targetId, invitationId, userId, committeeId);
    break;

    case this.DeepLinkingMethods.DiasporaCommitteeMember:
      this.assignUserAsCommitteeMember(targetId, invitationId, userId, committeeId);
    break;
  }
}

