$actionHelper.onNotificationReloadBasicUserInfo:
function(changedSubject) {  this.getGraphQL(`query {
    user(id: $dynamicUiVariables->initialDataSource.profileId) {
      id
      firstName
      lastName

      ...userImage
      ...userPlaceDetails
      ...userRoles
      ...userSector
      ...userMunicipalityDetails

      statistics {
        followers
        following
      }            

    }
  }`)
  .then( data => {
    const userDetails = _.get(data, 'user', null);
    const userRoles = _.get(data, "user.roles", []);
    const companiesRoles = userRoles.filter(x => x.entity.__typename === 'Company');
    const organizationRoles = userRoles.filter(x => x.entity.__typename === 'Organization'); 

    const initialDataSource= this.uiDefinition.initialDataSource;

    initialDataSource.firstName = _.get(data, 'user.firstName', null);
    initialDataSource.lastName = _.get(data, 'user.lastName', null);
    initialDataSource.fullName = initialDataSource.firstName + ' ' + initialDataSource.lastName;

    initialDataSource.photoCover = _.get(data, 'user.photoCover', null);
    initialDataSource.profileImage = _.get(data, 'user.photoCover.imageThumbnailUrl', null);
    initialDataSource.profileCoverImage = _.get(data, 'user.municipality.municipality.photoCover.imageThumbnailUrl', '');

    initialDataSource.placeOfOrigin = _.get(data, 'user.placeOfOrigin', null);
    initialDataSource.placeOfResidence = _.get(data, 'user.placeOfResidence', null);
    initialDataSource.municipality = _.get(data, 'user.municipality', null);

    initialDataSource.userRoles = userRoles;
    initialDataSource.hasCompanyRole = companiesRoles.length > 0 ? true : false;
    initialDataSource.hasOrganizationRole = organizationRoles.length > 0 ? true : false;
    initialDataSource.hasAmbassadorDiplomaticRole = userRoles.filter(userRole => (_.get(userRole, 'entity.__typename', '').toLowerCase() === 'diplomaticmission') && (_.get(userRole, 'role.role', '').toLowerCase() === 'president')).length > 0 ? true : false;
    initialDataSource.hasTownRole = userRoles.filter(userRole => (((_.get(userRole, 'entity.__typename', '') || '').toLowerCase() === 'municipality') && ((_.get(userRole, 'role.role', '') || '').toLowerCase() === 'electedmember'))).length > 0 ? true : false; 
    initialDataSource.hasSector = _.get(data, "user.sector", null) ? true : false;


    // prepare data
    initialDataSource.userBadges = this.$dynamicUiExtraService.prepareUserBadges(userDetails, true);
    initialDataSource.entityRoles = this.$dynamicUiExtraService.mapUserRoleToEntityRoles(userRoles);
  })
}

