$dynamicUiExtraService.mapNotificationToItem:
function(x) {  const objectType = this.getObjectType(x);
  let objectName;
  let objectImage;
  let conversationTargetType;
  let conversationTargetId;

  if(objectType === 'User') {
    objectName = this.getUserObjectName(x);
    objectImage = this.getObjectImage(x);
  } else if (objectType === 'Conversation') {
    objectName = this.getConversationOwnerName(x);
    objectImage = this.getConversationOwnerImage(x);
    conversationTargetType = _.get(x, "object.fromMailBoxId", null) === _.get(x, "object.receiver.id", null) ? _.get(x, "object.sender.owner.entity.__typename", null) : _.get(x, "object.receiver.owner.entity.__typename", null) ;
    conversationTargetId = _.get(x, "object.fromMailBoxId", null) === _.get(x, "object.receiver.id", null) ? _.get(x, "object.sender.owner.entity.id", null) : _.get(x, "object.receiver.owner.entity.id", null) ;         
  } else if (objectType === 'Comment') {
    objectName = this.getCommentnOwnerName(x);
    objectImage = this.getCommentnOwnerImage(x);
  } else {
    objectName = this.getObjectName(x);
    objectImage = this.getObjectImage(x);
  }

  return {
    id: this.getNotificationId(x), 
    message: this.getNotificationMessage(x), 
    createdDate: this.getNotificationCreatedDate(x),
    readed: this.checkNotificationReaded(x),

    objectId: this.getObjectId(x),
    parentDomainId: objectType === 'OrganizationCommittee' 
            ? this.getOrganizationObjectId(x) : 
                objectType === 'DiplomaticMissionCommittee' 
                  ? this.getDiplomaticObjectId(x) : this.getObjectId(x), 

    objectType: objectType,
    objectName:  objectName,
    objectImage: objectImage,

    conversationTargetType: objectType === 'Conversation' ? conversationTargetType : null,
    conversationTargetId: objectType === 'Conversation' ? conversationTargetId : null,
    conversationSenderMailboxId: objectType === 'Conversation' ? _.get(x, "object.fromMailBoxId", null) : null,
    conversationReceiverMailboxId: objectType === 'Conversation' ? _.get(x, "object.toMailBoxId", null) : null,

    commentActivityType: objectType === 'Comment' ? _.get(x, "object.listUnder.entity.__typename", null) : null,
    commentActivityId: objectType === 'Comment' ? _.get(x, "object.listUnder.entity.id", null) : null,
  }
}

