$dynamicUiExtraService.mapToConversationSummary:
function(x, selectedMailbox) {  const conversationMessage = this.getConversationMessage(x);
  const conversationReceiver = this.getConversationReceiver(x);
  const conversationSender = this.getConversationSender(x);
  let conversationMessageSender;
  let conversationMessageReceiver;

  if(conversationMessage) {
    conversationMessageSender = this.getConversationMessageSender(conversationMessage);
    conversationMessageReceiver = this.getConversationReceiver(conversationMessage);
  }

  const response =  {
    conversationId: this.getConversationId(x),

    lastMessageId: conversationMessage ? conversationMessage.id: null,
    lastMessageText: conversationMessage ? conversationMessage.text: "",
    lastMessageDate: conversationMessage ? conversationMessage.createdDate: "",
    lastMessageSenderId: conversationMessage ? (conversationMessageSender ? conversationMessageSender.owner.entity.id : null): null,
    lastMessageSenderType: conversationMessage ? (conversationMessageSender ? conversationMessageSender.owner.entity.__typename : null): null,

    lastMessageReceiverId: conversationMessage ? (conversationMessageReceiver ? conversationMessageReceiver.owner.entity.id : null): null,
    lastMessageReceiverType: conversationMessage ? (conversationMessageReceiver ? conversationMessageReceiver.owner.entity.__typename : null): null,

    receiverMailboxId: conversationReceiver ? this.getMailboxId(conversationReceiver) : null, 
    receiverMailboxOwnerId: conversationReceiver ? this.getMailboxOwnerId(conversationReceiver) : null, 
    receiverMailboxOwnerType: conversationReceiver ? this.getMailboxOwneType(conversationReceiver) : null,
    receiverMailboxOwnerName: conversationReceiver ?  (this.getMailboxOwneType(conversationReceiver) === 'User' ? this.getMailboxOwnerUserName(conversationReceiver) : this.getMailboxOwnerName(conversationReceiver)) :"" ,
