$dynamicUiExtraService.prepareNotificationSettings:
function prepareNotificationSettings(notificationSettings) {  const notificationSectionSettings = [];
  const notificationSettingsGroup = this.groupByPipe.transform(notificationSettings, 'group');

  if (notificationSettingsGroup) {
    notificationSettingsGroup.forEach(
      (notificationSection) => {
        if (notificationSection.key.toLowerCase() === 'general') {
          notificationSection.value.forEach(newNotificationSection => {
            if (newNotificationSection.group.toLowerCase() === 'general') {
              notificationSectionSettings.push(new this.NotificationSettingsGroup(newNotificationSection.description, [], newNotificationSection.pushEnabled, newNotificationSection.emailEnabled));
            }
          });
        } else {
          notificationSection.pushEnabled = notificationSection.value[0].pushEnabled ? notificationSection.value[0].pushEnabled : false;
          notificationSection.emailEnabled = notificationSection.value[0].emailEnabled ? notificationSection.value[0].emailEnabled : false;
          notificationSectionSettings.push(notificationSection);
        }
      }
    );
  }

  return notificationSectionSettings;
}

