$dynamicUiExtraService.prepareUserBadges:
function prepareUserBadges(dataSource, hasAdminRole) {  const domainBadges = [];
  const id = _.get(dataSource, 'id');
  const followersCount = _.get(dataSource, 'statistics.followers', 0);
  const followingCount = _.get(dataSource, 'statistics.following', 0);

  const business_Label = this.dynamicUiExternalResourcesService.get('profile-page.my-profile.about.badge.business-label');
  const network_Label = this.dynamicUiExternalResourcesService.get('profile-page.my-profile.about.badge.network-label');

  const countryOfOriginCode = _.get(dataSource, 'placeOfOrigin.country.isoCode', "").toLowerCase();
  const cityOfOriginName = _.get(dataSource, 'placeOfOrigin.city.name', "");
  const countryOfResidenceCode = _.get(dataSource, 'placeOfResidence.country.isoCode', "").toLowerCase();
  const cityOfResidenceName = _.get(dataSource, 'placeOfResidence.city.name', "");

  const hasDiplomaticRole = _.get(dataSource, 'roles', []).filter(userRole => ((_.get(userRole, 'entity.__typename', '') || '').toLowerCase() === 'diplomaticmission') && ((_.get(userRole, 'role.role', '') || '').toLowerCase() === 'president')).length > 0 ? true : false; 
  const hasTownRole = _.get(dataSource, 'roles', []).filter(userRole => (((_.get(userRole, 'entity.__typename', '') || '').toLowerCase() === 'municipality') && ((_.get(userRole, 'role.role', '') || '').toLowerCase() === 'electedmember'))).length > 0 ? true : false; 

  const diplomaticRole = hasDiplomaticRole ? _.get(dataSource, 'roles', []).find(userRole => ((_.get(userRole, 'entity.__typename', '') || '').toLowerCase() === 'diplomaticmission') && ((_.get(userRole, 'role.role', '') || '').toLowerCase() === 'president')) : null;
  const townRole = hasTownRole ? _.get(dataSource, 'roles', []).find(userRole => (((_.get(userRole, 'entity.__typename', '') || '').toLowerCase() === 'municipality') && ((_.get(userRole, 'role.role', '') || '').toLowerCase() === 'electedmember'))) : null;

  let municipalityId;
  let companyRoleBadge;
  let organizationRoleBadge;


  let companyRole = null;
  const featuredCompanyRole = _.get(dataSource, 'roles', []).find(userRole => ((_.get(userRole, 'entity.__typename', '') || '').toLowerCase() === 'company') && userRole.role.featured );
  if(featuredCompanyRole) {
    companyRole = featuredCompanyRole;
  } else {
    companyRole =  _.get(dataSource, 'roles', []).find(userRole => ((_.get(userRole, 'entity.__typename', '') || '').toLowerCase() === 'company'));
  }

  let organizationRole = null;
  const featuredOrganizationRole = _.get(dataSource, 'roles', []).find(userRole => (((_.get(userRole, 'entity.__typename', '') || '').toLowerCase() === 'organization') || ((_.get(userRole, 'entity.__typename', '') || '').toLowerCase() === 'organizationcommittee'))  && userRole.role.featured );
  if(featuredOrganizationRole) {
    organizationRole = featuredOrganizationRole;
  } else {
    organizationRole =  _.get(dataSource, 'roles', []).find(userRole => ((_.get(userRole, 'entity.__typename', '') || '').toLowerCase() === 'organization') || ((_.get(userRole, 'entity.__typename', '') || '').toLowerCase() === 'organizationcommittee'));
  }

  if(countryOfOriginCode === 'lb') {
    municipalityId = _.get(dataSource, 'municipality.municipality.id', null);
  }

  if(companyRole) {
    companyRoleBadge = new this.BadgeModel(companyRole.role.positionName, this.BadgeType.LogoType, companyRole.entity.name, companyRole.entity.id, companyRole.entity.name, (companyRole.entity.photoCover ? companyRole.entity.photoCover.imageThumbnailUrl : null) , "company/" + companyRole.entity.id);
  } else {
    if(hasAdminRole) {
    companyRoleBadge = new this.BadgeModel(business_Label, this.BadgeType.LogoType, null, null, null, "assets/icon/dynamic-ui/add_new_company.svg", "searchForCompany", {'companies': []});
    }
  }

  if(organizationRole) {
    if(organizationRole.entity.__typename === 'OrganizationCommittee') {
      organizationRoleBadge = new this.BadgeModel(organizationRole.role.positionName, this.BadgeType.LogoType, organizationRole.entity.organization.organization.name, organizationRole.entity.organization.organization.id, organizationRole.entity.organization.organization.name, (organizationRole.entity.organization.organization.photoCover ? organizationRole.entity.organization.organization.photoCover.imageThumbnailUrl : null), "organization/" + organizationRole.entity.organization.organization.id);
    } else {
      organizationRoleBadge = new this.BadgeModel(organizationRole.role.positionName, this.BadgeType.LogoType, organizationRole.entity.name, organizationRole.entity.id, organizationRole.entity.name, (organizationRole.entity.photoCover ? organizationRole.entity.photoCover.imageThumbnailUrl : null), "organization/" + organizationRole.entity.id);
    }
  } else {
    if(hasAdminRole) {
      organizationRoleBadge = new this.BadgeModel(network_Label, this.BadgeType.LogoType, null, null, null, "assets/icon/dynamic-ui/add_new_organization.svg", "searchForOrganization", {'organizations': []});
    }
  }

  if(hasTownRole) {
    if(countryOfResidenceCode && cityOfResidenceName) {
      const countryOfResidenceBadge = new this.BadgeModel(cityOfResidenceName, this.BadgeType.FlagType, null, id, name, countryOfResidenceCode.toLowerCase(), null);  
      domainBadges.push(countryOfResidenceBadge);
    }  

    let positionNameLabel = townRole.role.positionName.toLowerCase() == 'electedmember' ? 'Elected Member' : ( townRole.role.positionName.toLowerCase() == 'vicepresident' ? 'Vice President': townRole.role.positionName);

    const townBadge = new this.BadgeModel(positionNameLabel, this.BadgeType.LogoType, followersCount, id, null, "assets/icon/dynamic-ui/Networks_Badge_Gold.svg", null);
    domainBadges.push(townBadge);

    if(organizationRoleBadge) {
      domainBadges.push(organizationRoleBadge);
    }

    if(companyRoleBadge) {
      domainBadges.push(companyRoleBadge);
    }
  } 
  else if (hasDiplomaticRole) {
    if(countryOfOriginCode && cityOfOriginName) {
      const countryOfOriginBadge = new this.BadgeModel(cityOfOriginName, this.BadgeType.FlagType, null, municipalityId, name, countryOfOriginCode.toLowerCase(), municipalityId? ("town/"  + municipalityId ): null);  
      domainBadges.push(countryOfOriginBadge);
    }

    if(countryOfResidenceCode && cityOfResidenceName) {
      const countryOfResidenceBadge = new this.BadgeModel(cityOfResidenceName, this.BadgeType.FlagType, null, null, name, countryOfResidenceCode.toLowerCase(), null);  
      domainBadges.push(countryOfResidenceBadge);
    }  

    const diplomaticBadge = new this.BadgeModel(diplomaticRole.role.positionName, this.BadgeType.LogoType, followersCount, null, null, "assets/icon/dynamic-ui/Networks_Badge_Gold.svg", "diplomaticMission/" + diplomaticRole.entity.id);
    const followingBadge = new this.BadgeModel("Reach", this.BadgeType.TextType, followingCount, id, null, null, ('profile/viewFollowers/' + id));

    domainBadges.push(diplomaticBadge);
    domainBadges.push(followingBadge);

  } 
  else {
    if(countryOfOriginCode && cityOfOriginName) {
      const countryOfOriginBadge = new this.BadgeModel(cityOfOriginName, this.BadgeType.FlagType, null, municipalityId, name, countryOfOriginCode.toLowerCase(),  municipalityId? ("town/"  + municipalityId ): null);  
      domainBadges.push(countryOfOriginBadge);
    }

    if(countryOfResidenceCode && cityOfResidenceName) {
      const countryOfResidenceBadge = new this.BadgeModel(cityOfResidenceName, this.BadgeType.FlagType, null, null, name, countryOfResidenceCode.toLowerCase(), null);  
      domainBadges.push(countryOfResidenceBadge);
    }    

    if(organizationRoleBadge) {
      domainBadges.push(organizationRoleBadge);
    }
    if(companyRoleBadge) {
      domainBadges.push(companyRoleBadge);
    }
  }    


  return domainBadges;
}

