$dynamicUiExtraService.prepareCompanyParentBranches:
function prepareCompanyParentBranches(dataSource) {
  const parents = dataSource.company.parent;
  let rootBranchId = dataSource.company.id;

  if(!parents || parents.length === 0) {
    return {rootBranchId: rootBranchId, hasParentBranch: false, parentBranchId: null};
  }

  const parentBranch = this.getAllParentBranches(parents[0]);
  let parentBranchId = null;

  if(dataSource.company && dataSource.company.parent && dataSource.company.parent.length > 0)
    parentBranchId = dataSource.company.parent[0].id;

  if(parentBranch) {
    return {rootBranchId: parentBranch.id, hasParentBranch: true, parentBranchId: parentBranchId };
  }

  return {rootBranchId: rootBranchId, hasParentBranch: false, parentBranchId: null};
}

$dynamicUiExtraService.getAllParentBranches:
function getAllParentBranches(branch) {  if(!branch.parent || branch.parent.length === 0) { return branch; }
