  }

  return branchesTree;
}

$dynamicUiExtraService.checkIfTreeContainsChildBranches:
function checkIfTreeContainsChildBranches(tree, childs, currentCompanyId) {  if (tree.targetId === currentCompanyId) {
    return true;
  }

  for (const child of childs) {
    const result = this.checkIfTreeContainsChildBranches(child, child.children, currentCompanyId);

    if (result) {
      tree.containsChild = true;
      return result;
    }
  }
  return false;
}

