function prepareDiplomaticMissionBadges(dataSource, hasAdminRole) {  const domainBadges = [];

  const id = _.get(dataSource, 'id');
  const name = _.get(dataSource, 'name');
  const followersCount = _.get(dataSource, 'statistics.followers', 0);
  const diplomatsMemberCount = _.get(dataSource, 'statistics.keyStaff', 0) + _.get(dataSource, 'statistics.diplomats', 0);
  const countryCode = _.get(dataSource, 'place.country.isoCode', "");
  const cityName = _.get(dataSource, 'place.city.name', "");
  const cityId = _.get(dataSource, 'place.city.lookupId', "");

  if(countryCode && cityName) {
    const countryBadge = new this.BadgeModel(cityName, this.BadgeType.FlagType, null, id, name, countryCode.toLowerCase(), null);
    domainBadges.push(countryBadge);
  }

  const followerBadge = new this.BadgeModel(followersCount === 1 ? this.dynamicUiExternalResourcesService.get('diplomatic-page.diplomatic.about.badges.follower') : this.dynamicUiExternalResourcesService.get('diplomatic-page.diplomatic.about.badges.followers'), this.BadgeType.TextType, followersCount, id, name, null, 'diplomaticMission/followers/' + id);
  const diplomatBadge = new this.BadgeModel(diplomatsMemberCount === 1 ? this.dynamicUiExternalResourcesService.get('diplomatic-page.diplomatic.about.badges.diplomat') : this.dynamicUiExternalResourcesService.get('diplomatic-page.diplomatic.about.badges.diplomats'), this.BadgeType.TextType, diplomatsMemberCount, id, name, null, ('diplomaticMission/viewDiplomaticKeyStaff/' + id));

  domainBadges.push(followerBadge);
  domainBadges.push(diplomatBadge);

  return domainBadges;
}

