$dynamicUiExtraService.prepareTownGeoLocation:
function prepareTownGeoLocation(dataSource) {  const locationDetails = [];
  let distanceFromBeirut = _.get(dataSource, 'distanceFromBeirut');
  let surface = _.get(dataSource, 'surface');
  let altitude = _.get(dataSource, 'altitude');

  if (distanceFromBeirut || distanceFromBeirut == 0) {
    const distanceLabel = this.dynamicUiExternalResourcesService.get('town-page.town.geo-location.distance');
    const distanceInfo = new this.GeoLocationModel(this.AppIcons.contact_distane, distanceFromBeirut + distanceLabel);
    locationDetails.push(distanceInfo);
  }

  if (surface || surface == 0) {
    const surfaceLabel = this.dynamicUiExternalResourcesService.get('town-page.town.geo-location.surface');
    const surfaceInfo = new this.GeoLocationModel(this.AppIcons.contact_altitude, surface + surfaceLabel);
    locationDetails.push(surfaceInfo);
  }

  if (altitude || altitude == 0) {
    const altitudeLabel = this.dynamicUiExternalResourcesService.get('town-page.town.geo-location.altitude');
    const altitudeInfo = new this.GeoLocationModel(this.AppIcons.contact_surface, altitude + altitudeLabel);
    locationDetails.push(altitudeInfo);
  }

  return locationDetails;
}

