$dynamicUiExtraService.openSectorsModal:
async function(title, selectedController, formGroup, options) {  const modal = await this.modalController.create({
    component: this.SectorComponent,
    componentProps: { 'title': title, 'sectors': Array.from(options), 'withSearch': true, 'canClearSelectedValue': selectedController.withNullValue }
  });

  await modal.present();

  modal.onDidDismiss().then((data) => {
    let isSelected = _.get(data, 'data.withOption');
    let isClear =  _.get(data, 'data.isClear')
    if (isSelected) {
      if (isClear) {
        formGroup.controls[selectedController.key].setValue(null);
        selectedController.value = null;
      } else {
        const selectedSector = _.get(data, 'data.selectOption');
        formGroup.controls[selectedController.key].setValue(selectedSector.sectorId);
        selectedController.value = selectedSector.title;
      }
    }
  });
}

$dynamicUiExtraService.openCountriesModal:
async function(title, selectedController, formGroup, options, isPhoneCountry) {  const modal = await this.modalController.create({
    component: this.ModalComponent,
    componentProps: { 'title': title, 'options': Array.from(options), 'withSearch': true, 'isCountry': true, 'isPhoneCountry': isPhoneCountry }
  });

  await modal.present();

  modal.onDidDismiss().then((data) => {
    let isSelected = _.get(data, 'data.withOption');
    if (isSelected) {
      let selectOption = _.get(data, 'data.selectOption');
      selectedController.value = isPhoneCountry ? selectOption.code : selectOption.name;
      selectedController.leftIcon = isPhoneCountry ? selectOption.iso.toLowerCase() : selectOption.isoCode.toLowerCase();
      formGroup.controls[selectedController.key].setValue(isPhoneCountry ? selectOption.code : selectOption.lookupId);
      if(isPhoneCountry) {
        formGroup.controls[selectedController.countryIsoCodeKey].setValue(selectOption.iso);
      }
    }
  });
}

