$dynamicUiExtraService.getEventDate:
function(filter, $searchService) {  if(filter.value) {
    if(filter.value === 'upcomingStartDate') {
      $searchService.searchParams.upcomingStartDate = new Date(); 
      $searchService.searchParams.pastEndDate = null;
    } else {
      $searchService.searchParams.upcomingStartDate = null; 
      $searchService.searchParams.pastEndDate = new Date();
    }
  } else {
      $searchService.searchParams.upcomingStartDate = null; 
      $searchService.searchParams.pastEndDate = null;
  } 
}

