$dynamicUiExtraService.changePassword:
function($actionHelper, oldPassword, newPassword, confirmPassword) {    const changePasswordRequest = {'oldPassword': oldPassword, 'newPassword': newPassword, 'confirmPassword': confirmPassword};
    const params =$actionHelper.FetchConfig.postConfig;
    params.body = JSON.stringify(changePasswordRequest);
    params.headers.Authorization = 'Bearer ' + $actionHelper.$authenticationService.getAccessToken();

    $actionHelper.$loaderService.present().then(res => {
      $actionHelper.fetchData(query, params).then(() => {
        $actionHelper.showToast('Success! You may now login with your new password');
        $actionHelper.goBackPage();
      }).catch((error) => {
          if (error.status === 400) {
            $actionHelper.showToast('Incorrect old password. please retry');
          }
      }).finally(()=> {
        $actionHelper.$loaderService.dismiss();
      });
    });
}

$dynamicUiExtraService.getPeoplesInYourSector:
function(getGraphQL, result, hasSector) {  if(!hasSector) {
    return;
  }
  getGraphQL(`
    query { 
      search {
        directory {
          peopleDirectory {
            inYourSector(first: 9) {
              items {
                ...userCommonProperties
              }  
            }
          }
        }
      }   
    }
  `).then(
    (data) => {
      const peoplesInYourSector = _.get(data, "search.directory.peopleDirectory.inYourSector.items", []).map(x => this.mapSearchPeopleToItem(x));
      result.peopleDirectory.peoplesInYourSector = peoplesInYourSector;
      result.peopleDirectory.hasPeoplesInYourSector = peoplesInYourSector.length > 0
    }
  );
}

